import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { Room, User } from "@/lib/types";

interface ProblemReportModalProps {
  room: Room;
  currentUser: User;
  isOpen: boolean;
  onClose: () => void;
}

export default function ProblemReportModal({
  room,
  currentUser,
  isOpen,
  onClose,
}: ProblemReportModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [formData, setFormData] = useState({
    type: "",
    priority: "media",
    description: "",
    location: "",
  });

  const problemTypes = [
    { value: "eletrico", label: "Problema ElÃ©trico" },
    { value: "hidraulico", label: "Problema HidrÃ¡ulico" },
    { value: "ar_condicionado", label: "Ar Condicionado" },
    { value: "moveis", label: "MÃ³veis/DecoraÃ§Ã£o" },
    { value: "eletronicos", label: "EletrÃ´nicos" },
    { value: "limpeza_especial", label: "Limpeza Especial" },
    { value: "outros", label: "Outros" },
  ];

  const priorities = [
    { value: "baixa", label: "Baixa", color: "text-green-600" },
    { value: "media", label: "MÃ©dia", color: "text-yellow-600" },
    { value: "alta", label: "Alta", color: "text-red-600" },
    { value: "urgente", label: "Urgente", color: "text-red-800 font-bold" },
  ];

  // Submit problem report mutation
  const submitProblemMutation = useMutation({
    mutationFn: async () => {
      if (!formData.type || !formData.description.trim()) {
        throw new Error("Tipo e descriÃ§Ã£o sÃ£o obrigatÃ³rios.");
      }

      return await apiRequest("POST", "/api/problem-reports", {
        roomId: room.id,
        reportedBy: currentUser.id,
        type: formData.type,
        priority: formData.priority,
        description: formData.description,
        location: formData.location,
        photos: [], // TODO: Implement photo upload
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/rooms"] });
      queryClient.invalidateQueries({ queryKey: ["/api/problem-reports"] });
      toast({
        title: "Problema reportado",
        description: `Problema no quarto ${room.number} reportado com sucesso. O status foi alterado para "Para InspeÃ§Ã£o".`,
      });
      onClose();
      resetForm();
    },
    onError: (error) => {
      toast({
        title: "Erro",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      type: "",
      priority: "media",
      description: "",
      location: "",
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    submitProblemMutation.mutate();
  };

  const handleClose = () => {
    onClose();
    resetForm();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-2xl" data-testid="problem-report-modal">
        <DialogHeader className="pb-4 border-b">
          <DialogTitle className="text-xl font-bold">
            Reportar Problema
          </DialogTitle>
          <DialogDescription>
            Quarto {room.number} - Descreva o problema encontrado no quarto para que a equipe de manutenÃ§Ã£o possa resolvÃª-lo.
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6 p-6">

          <div>
            <Label htmlFor="problemType" className="text-sm font-medium text-foreground mb-2 block">
              Tipo de Problema
            </Label>
            <Select
