import { useState } from "react";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { Room } from "@/lib/types";

interface RoomStatusModalProps {
  room: Room;
  children: React.ReactNode;
}

// Mapeamento dos status internos para os nomes em portuguÃªs
const statusMap = {
  dirty: "Ocupado e Sujo",
  clean: "Limpo", 
  occupied: "Ocupado Limpo",
  cleaning: "Vazio",
  inspection: "DisponÃ­vel para o check in",
  approved: "Aprovado"
} as const;

const statusOptions = [
  { value: "dirty", label: "Ocupado e Sujo" },
  { value: "clean", label: "Limpo" },
  { value: "occupied", label: "Ocupado Limpo" },
  { value: "cleaning", label: "Vazio" },
  { value: "inspection", label: "DisponÃ­vel para o check in" },
  { value: "approved", label: "Aprovado" }
];

export default function RoomStatusModal({ room, children }: RoomStatusModalProps) {
  const [open, setOpen] = useState(false);
  const [selectedStatus, setSelectedStatus] = useState(room.status);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const updateStatusMutation = useMutation({
    mutationFn: async (newStatus: string) => {
      return apiRequest("PATCH", `/api/rooms/${room.id}/status`, { status: newStatus });
    },
    onSuccess: () => {
      // Invalidate all room queries (including those with user-specific keys)
      queryClient.invalidateQueries({ queryKey: ["/api/rooms"] });
      // Also force a refetch on the specific room data
      queryClient.refetchQueries({ queryKey: ["/api/rooms"] });
      toast({
        title: "Status atualizado",
        description: `Quarto ${room.number} alterado para: ${statusMap[selectedStatus as keyof typeof statusMap]}`,
      });
      setOpen(false);
    },
    onError: (error: any) => {
      toast({
        title: "Erro",
        description: "Falha ao atualizar status do quarto",
        variant: "destructive",
      });
      console.error("Error updating room status:", error);
    },
  });

  const handleStatusUpdate = () => {
    if (selectedStatus !== room.status) {
      updateStatusMutation.mutate(selectedStatus);
    } else {
      setOpen(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        {children}
      </DialogTrigger>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Alterar Status - Quarto {room.number}</DialogTitle>
        </DialogHeader>
        <div className="space-y-4 py-4">
          <div className="space-y-2">
            <label className="text-sm font-medium">Status Atual:</label>
            <p className="text-sm text-muted-foreground">
              {statusMap[room.status as keyof typeof statusMap]}
            </p>
          </div>

          <div className="space-y-2">
            <label className="text-sm font-medium">Novo Status:</label>
            <Select value={selectedStatus} onValueChange={setSelectedStatus}>
              <SelectTrigger data-testid="status-select">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {statusOptions.map((option) => (
                  <SelectItem 
                    key={option.value} 
                    value={option.value}
                    data-testid={`status-option-${option.value}`}
                  >
                    {option.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </div>

        <div className="flex justify-end space-x-2">
          <Button
            variant="outline"
            onClick={() => setOpen(false)}
            data-testid="cancel-status-change"
          >
            Cancelar
          </Button>
          <Button
            onClick={handleStatusUpdate}
            disabled={updateStatusMutation.isPending}
            data-testid="confirm-status-change"
          >
            {updateStatusMutation.isPending ? "Alterando..." : "Confirmar"}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/approval-modal.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { CheckCircle, X } from "lucide-react";
import { Room, User } from "@shared/schema";

interface ApprovalModalProps {
  isOpen: boolean;
  onClose: () => void;
  room: Room;
  onApprovalComplete: () => void;
}

export function ApprovalModal({ isOpen, onClose, room, onApprovalComplete }: ApprovalModalProps) {
  const { toast } = useToast();

  // Fetch completed checklist for this room
  const { data: checklistCompletion, isLoading: checklistLoading } = useQuery({
    queryKey: ["/api/checklist-completions/room", room.id],
    queryFn: async () => {
      const response = await fetch(`/api/checklist-completions/room/${room.id}`);
      if (!response.ok) throw new Error("Failed to fetch checklist");
      return response.json();
    },
    enabled: isOpen,
  });

  // Fetch checklist template
  const { data: template, isLoading: templateLoading } = useQuery({
    queryKey: ["/api/checklist-template", "Limpeza 5 Estrelas"],
    queryFn: async () => {
      const response = await fetch("/api/checklist-template/Limpeza 5 Estrelas");
      if (!response.ok) throw new Error("Failed to fetch template");
      return response.json();
    },
    enabled: isOpen && checklistCompletion,
  });

  // Fetch users for housekeeper/supervisor names
  const { data: users = [] } = useQuery({
    queryKey: ["/api/users"],
    enabled: isOpen && checklistCompletion,
  });

  const approveRoomMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("PATCH", `/api/rooms/${room.id}`, {
        status: "approved"
      });
    },
    onSuccess: () => {
      toast({
        title: "Quarto aprovado",
        description: `Quarto ${room.number} foi aprovado com sucesso.`,
      });
      onApprovalComplete();
      onClose();
    },
    onError: (error) => {
      toast({
        title: "Erro",
        description: error.message || "Falha ao aprovar o quarto.",
        variant: "destructive",
      });
    },
  });

  const getStepName = (step: string) => {
    const stepNames: Record<string, string> = {
      arrumacao: "ArrumaÃ§Ã£o",
      limpeza: "Limpeza",
      organizacao: "OrganizaÃ§Ã£o",
      verificacao: "VerificaÃ§Ã£o",
      finalizacao: "FinalizaÃ§Ã£o"
    };
    return stepNames[step] || step;
  };

  const isLoading = checklistLoading || templateLoading;

  if (isLoading) {
    return (
      <Dialog open={isOpen} onOpenChange={onClose}>
        <DialogContent className="max-w-4xl max-h-[90vh] overflow-hidden">
          <div className="flex items-center justify-center p-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
            <span className="ml-2">Carregando checklist...</span>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  if (!checklistCompletion) {
    return (
      <Dialog open={isOpen} onOpenChange={onClose}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Erro</DialogTitle>
            <DialogDescription>
              Nenhum checklist finalizado encontrado para o quarto {room.number}.
            </DialogDescription>
          </DialogHeader>
          <div className="flex justify-end">
            <Button onClick={onClose}>Fechar</Button>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  const housekeeper = (users as User[]).find((u: User) => u.id === checklistCompletion.housekeeperId);
  const supervisor = (users as User[]).find((u: User) => u.id === checklistCompletion.supervisorId);
  const totalItems = template?.items ? Object.values(template.items).flat().length : 0;
  const completedCount = Object.values(checklistCompletion.completedItems).filter(Boolean).length;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-hidden" data-testid="approval-modal">
        <DialogHeader className="pb-4 border-b">
          <DialogTitle className="text-2xl font-bold flex items-center">
            <CheckCircle className="h-6 w-6 text-green-600 mr-2" />
            AprovaÃ§Ã£o - Quarto {room.number}
          </DialogTitle>
          <DialogDescription>
            Revise o checklist concluÃ­do antes de aprovar o quarto
          </DialogDescription>
        </DialogHeader>

        <div className="overflow-y-auto max-h-[calc(90vh-200px)] p-6">
          {/* Checklist Info */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6 p-4 bg-secondary rounded-lg">
            <div>
              <p className="text-sm font-medium text-muted-foreground">Camareira(o):</p>
              <p className="font-semibold">{housekeeper?.name || 'N/A'}</p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">Supervisor:</p>
              <p className="font-semibold">{supervisor?.name || 'N/A'}</p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">ConcluÃ­do em:</p>
              <p className="font-semibold">
                {checklistCompletion.completedAt 
                  ? new Date(checklistCompletion.completedAt).toLocaleDateString('pt-BR')
                  : 'N/A'
                }
              </p>
            </div>
          </div>

          {/* Progress Summary */}
          <div className="mb-6 p-4 bg-green-50 border border-green-200 rounded-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-green-800">Status do Checklist</p>
                <p className="text-lg font-bold text-green-900">
                  {completedCount}/{totalItems} itens concluÃ­dos
                </p>
              </div>
              <Badge variant="outline" className="bg-green-100 text-green-800 border-green-300">
                100% Completo
              </Badge>
            </div>
          </div>

          {/* Checklist Items */}
          {template?.items && Object.entries(template.items as Record<string, any[]>).map(([step, items]: [string, any[]]) => (
            <div key={step} className="mb-6">
              <h3 className="text-lg font-semibold mb-3 flex items-center">
                <CheckCircle className="h-5 w-5 text-green-600 mr-2" />
                {getStepName(step)}
              </h3>
              <div className="space-y-2">
                {items.map((item: any, index: number) => {
                  const itemKey = `${step}-${index}`;
                  const isCompleted = checklistCompletion.completedItems[itemKey];
                  return (
                    <div key={itemKey} className="flex items-center p-3 bg-white border rounded-lg">
                      <div className="flex-shrink-0 mr-3">
                        <CheckCircle className="h-5 w-5 text-green-600" />
                      </div>
                      <div className="flex-grow">
                        <p className="font-medium">{item.title}</p>
                        {item.description && (
                          <p className="text-sm text-muted-foreground">{item.description}</p>
                        )}
                      </div>
                      <Badge variant="outline" className="bg-green-100 text-green-800">
                        ConcluÃ­do
                      </Badge>
                    </div>
                  );
                })}
              </div>
              {step !== Object.keys(template.items)[Object.keys(template.items).length - 1] && (
                <Separator className="mt-4" />
              )}
            </div>
          ))}
        </div>

        {/* Action Buttons */}
        <div className="flex justify-between pt-4 border-t bg-background">
          <Button
            variant="outline"
            onClick={onClose}
            data-testid="button-cancel-approval"
          >
            <X className="h-4 w-4 mr-2" />
            Cancelar
          </Button>
          <Button
            className="bg-green-600 text-white hover:bg-green-700"
            onClick={() => approveRoomMutation.mutate()}
            disabled={approveRoomMutation.isPending}
            data-testid="button-confirm-approval"
          >
            <CheckCircle className="h-4 w-4 mr-2" />
            {approveRoomMutation.isPending ? "Aprovando..." : "Aprovar Quarto"}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}
